const Country = () => {
  const [countries, setCountries] = useState(data);
  const [search, setSearch] = useState("");

  const filteredCountries = countries.filter((country) =>
    country.name.common.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="flex flex-col items-center p-4">
      <input
        type="text"
        placeholder="Type a country name..."
        className="mb-4 p-2 border rounded"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      <div className="flex flex-wrap justify-center">
        {filteredCountries.map((country, index) => (
          <div
            key={index}
            className="max-w-xs m-4 p-6 bg-white rounded-lg shadow-lg transition-transform transform hover:scale-105"
          >
            <img src={country.img} alt="" className="h-[40vh] w-full" />
            <h2 className="text-2xl font-semibold mb-2">
              {country.name.common}
            </h2>
            <p className="text-gray-600">
              <strong>Capital:</strong> {country.capital}
            </p>
            <p className="text-gray-600">
              <strong>Population:</strong> {country.population}
            </p>
            <p className="text-gray-600">
              <strong>Area:</strong> {country.area} km¬≤
            </p>
            <p className="text-gray-600">
              <strong>Region:</strong> {country.region}
            </p>
            <p className="text-gray-600">
              <strong>Subregion:</strong> {country.subregion}
            </p>
            <p className="text-gray-600">
              <strong>Languages:</strong>{" "}
              {Object.values(country.languages).join(", ")}
            </p>
          </div>
        ))}
      </div>

      {filteredCountries.length === 0 && (
        <p className="text-red-500 mt-4">No countries found.</p>
      )}
    </div>
  );
};

export default Country;
